// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Weather from './Components/Weather';
import SearchBar from './Components/SearchBar';
import 'bootstrap/dist/css/bootstrap.min.css';
import './index.css';

const App = () => {
  const [weather, setWeather] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;

  const fetchWeather = async (query) => {
    try {
      const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather`, {
        params: {
          q: query,
          appid: apiKey,
        },
      });
      setWeather(response.data);
    } catch (error) {
      console.error("Error fetching weather data:", error);
      alert("Invalid location or unable to fetch data.");
    }
  };

  const toggleDarkMode = () => setDarkMode(!darkMode);

  useEffect(() => {
    // Optionally, fetch the weather for a default city on load
    fetchWeather('New York');
  }, []);

  return (
    <div className='container '>
<div className='row'>
  <div className='col-12 shadow   my-5'>
  <div className={`app ${darkMode ? 'dark-mode' : ''}  d-flex justify-content-center align-items-center`}>
      <div className="container p-5 mx-5">
        <h1 className='my-2'><i className="bi bi-cloud-sun"></i>Weather App</h1>
        <button onClick={toggleDarkMode} className="btn btn-secondary">
          {darkMode ? 'Light Mode' : 'Dark Mode'}
        </button>
        <SearchBar onSearch={fetchWeather} />
        <Weather weather={weather} />
      </div>
    </div>
  </div>
</div>
    </div>

  );
};

export default App;
